package org.hum.scaffold.tools;

public class HexTools {
	
	public static byte[] toBytes(String hexStream) {
		byte[] bytes = new byte[hexStream.length() / 2];
		int cursor = 0;

		for (int i = 0; i < hexStream.length(); i += 2) {
			String decimal = hexStream.substring(i, i + 2);
			bytes[cursor++] = (byte) Integer.parseInt(decimal, 16);
		}

		return bytes;
	}

	public static void main(String[] args) {
		String 	hexStream = "";
		System.out.println(new String(toBytes(hexStream)));
				hexStream = "";
		System.out.println(new String(toBytes(hexStream)));
		hexStream = "";
		System.out.println(new String(toBytes(hexStream)));
	}
}
